import React, { useState, useEffect, useContext } from 'react';
import api from '../api';
import { Context } from '../Context/AuthContext';
import history from '../history';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import EditIcon from '@material-ui/icons/Edit';
import Title from './template/Title';
import Layout from './template/Layout'

async function add() {
    history.push({
      pathname: '/adicionarPaciente'
    });
}
  
async function update(paciente_id) {
    try {
      const { data } = await api.get(`pacientes/${paciente_id}`);
      
      console.log("paciente");
      console.log(data);
  
      history.push({
        pathname: '/adicionarPaciente',
        state: data
      });
    } catch (err) {
      alert("Erro em editar");
    } 
  
    // history.push({
    //   pathname: '/editarPaciente/1',
    //   state: paciente_id,
    // });
}
  
async function remove(paciente_id) {
    history.push({
        pathname: '/editarPaciente/1',
        state: paciente_id,
    });
}

const useStyles = makeStyles((theme) => ({
    seeMore: {
        marginTop: theme.spacing(3),
    },
}));


export default function ListPaciente (){
    
    const classes = useStyles();
    const { handleLogout } = useContext(Context);
    const [usuario_id, setUsuario ] = useState();
    const [pacientes, setPacientes] = useState([]);
    const id = "";

    useEffect(() => {
        (async () => {


          const { data } = await api.get('/pacientes');
    
          setPacientes(data);
        })();
    }, []);


    return(
        <>
        <div>
            <Layout />
            <React.Fragment>
                <Title>Lista de Pacientes</Title>
                <Table size="small">
                    <TableHead>
                    <TableRow>
                        <TableCell>Nome</TableCell>
                        <TableCell>CPF</TableCell>
                        <TableCell align="right">Ações</TableCell>
                    </TableRow>
                    </TableHead>
                    <TableBody>
                    {pacientes.map((paciente) => (
                        <TableRow key={paciente.id}>
                            <TableCell>{paciente.nome}</TableCell>
                            <TableCell>{paciente.cpf}</TableCell>
                            <TableCell>
                                <a
                                    href=""
                                    onClick={(event) => {
                                        event.preventDefault();
                                        update(paciente.id);
                                    }}
                                >
                                <EditIcon />      
                                </a>
                                <a
                                    href=""
                                    onClick={(event) => {
                                        event.preventDefault();
                                        remove(paciente.id);
                                    }}
                                >
                                <EditIcon />      
                                </a>       
                            </TableCell>
                        </TableRow>
                    ))}
                    </TableBody>
                </Table>
                <div className={classes.seeMore}>
                    <Link color="primary" href="#" onClick={add}>
                        Adicionar
                    </Link>
                </div>
                </React.Fragment>
        </div>
        </>
    )
};

