import React, { useState, useEffect, useContext } from 'react';
import history from '../history';
import api from '../api';
import { TextField, Button } from "@material-ui/core";
import { Context } from '../Context/AuthContext';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Layout from './template/Layout'
import { SettingsRemoteOutlined, Update } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

// async function add(form) {

//   const nome = form.nome
//   const cpf = form.cpf

//   try {
//     const response = await api.post('/pacientes', { nome, cpf });
//     console.log(response);

//     alert("Cadastro realizado com sucesso!");
//     history.push({
//       pathname: '/pacientes'
//     });
//   } catch (err) {
//     alert("Erro ao Salvar paciente");
//   } 

// }

// async function update(form) {

//   const id = form.id  
//   const nome = form.nome
//   const cpf = form.cpf

//   try {
//     const response = await api.put(`pacientes/${id}`, { nome, cpf });
//     console.log(response);

//     alert("Cadastro Alterado com sucesso!");
//     history.push({
//       pathname: '/pacientes'
//     });
//   } catch (err) {
//     alert("Erro ao Salvar paciente");
//   } 

// }


export default function FormPaciente(dados) {
  const { handleLogout } = useContext(Context);
  const [prontuario, setProntuario ] = useState();
  const [titulo, setTitulo ] = useState(false);
  const [descricao, setDescricao ] = useState(false);
  const [pacientes, setPacientes] = useState();
  const [user_id, setUser ] = useState();
  const classes = useStyles();

  useEffect(() => {

    (async () => {
      const { pacientes } = await api.get('/pacientes');
      setPacientes(pacientes);
    })();
    

      if (typeof dados.location.state !== 'undefined'){
        console.log("editando esse Prontuario");
        
      }
  }, []);



  return (
    <>
      <Layout />
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <div className={classes.paper}>
          <Typography component="h1" variant="h5">
            Cadastro Paciente!
          </Typography>
          <form 
            onSubmit={(event) => {
              event.preventDefault();
            }}
            className={classes.form} 
            noValidate
          >
            <TextField
              variant="outlined"
              margin="normal"
              // value={ nome ? nome : ''}
              fullWidth
              id="nome"
              label="Nome Paciente"
              name="nome"
              onChange={(event) => {
                
              }}
            />
            <TextField
              variant="outlined"
              margin="normal"
              fullWidth
              label="CPF - Paciente"
              // value={ cpf ? cpf : ''}
              name="cpf"
              type="Text"
              id="cpf"
              onChange={(event) => {
                
              }}
            />
            {user_id
              ? 
                <Button
                  type="Submit"
                  variant="contained"
                  color="primary"
                  className={classes.submit}
                  onClick={(event) => {
                    
                  }}
                >
                  Editar
                </Button>
              : 
                <Button
                  type="Submit"
                  variant="contained"
                  color="primary"
                  className={classes.submit}
                  onClick={(event) => {
                    
                }}
              >
                Cadastrando
              </Button>
            }
            <Button
              type="Submit"
              variant="contained"
              className={classes.submit}
              onClick={(event) => {
                history.push('/Prontuarios');
              }}
            >
              Voltar
            </Button>
          </form>
        </div>
      </Container>
    </>
  );
}
