import React, { useState, useEffect, useContext } from 'react';
import api from '../api';
import { Context } from '../Context/AuthContext';
import history from '../history';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import DeleteIcon from '@material-ui/icons/Delete';
import Icon from '@material-ui/core/Icon';
import SaveIcon from '@material-ui/icons/Save';
import Layout from './template/Layout'



async function adionar() {
  history.push({
    pathname: '/adicionarPaciente'
  });

}

async function editar(paciente_id) {
  history.push({
    pathname: '/editarPaciente/1',
    state: paciente_id,
  });

}



export default function Users(usuario) {
  const { handleLogout } = useContext(Context);
  const [usuario_id, setUsuario ] = useState(usuario);
  const [pacientes, setPacientes] = useState([]);
  const id = "";


  useEffect(() => {
    (async () => {
      const id = localStorage.getItem('id');
      const name = localStorage.getItem('name');

      alert(usuario_id.location.state);
      console.log(usuario_id.location.state);
      console.log(usuario_id);
      const { data } = await api.get('/pacientes');

      setPacientes(data);
    })();
  }, []);

  return (
    <>
      <Layout /> 
      <ul>
        {pacientes.map((paciente) => (
          <li>
            {paciente.nome} ({paciente.cpf})
            <button type="button" onClick={alert(paciente.id)}>Adicionar</button> 
          </li>
        ))}
      </ul>
      <button type="button" onClick={adionar}>Adicionar</button>    
      <button type="button" onClick={handleLogout}>Sair</button>
    </> 
  );
}
